@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SelectFiles(navController: NavHostController) {
 var selectedTab by remember { mutableStateOf(2) } // Default to Photos tab
 val tabTitles = listOf(
 "Files", "Music", "Photos", "Videos", "Contacts", "Apps", "Documents", "Archives"
 )
 var isGrid by remember { mutableStateOf(true) }

 Scaffold(
 topBar = {
 TopAppBar(
 title = {
 Text(
 "Select " + tabTitles[selectedTab],
 style = MaterialTheme.typography.headlineSmall.copy(fontWeight = FontWeight.Bold),
 color = MaterialTheme.colorScheme.primary,
 modifier = Modifier.padding(start = 8.dp)
 )
 },
 navigationIcon = {
 IconButton(onClick = { navController.popBackStack() }) {
 Icon(Icons.Filled.ArrowBack, contentDescription = "Back")
 }
 }
 )
 },
 bottomBar = { SmartBottomBar(navController) }
 ) { internalPadding ->
 Column(
 modifier = Modifier
 .padding(internalPadding)
 .fillMaxSize()
 ) {
 // Tabs
 TabRow(
 selectedTabIndex = selectedTab,
 containerColor = MaterialTheme.colorScheme.surface
 ) {
 tabTitles.forEachIndexed { i, title ->
 Tab(
 selected = selectedTab == i,
 onClick = { selectedTab = i },
 text = { Text(title, fontSize = 14.sp) }
 )
 }
 }
 // Grid/List Toggle
 Row(
 Modifier.padding(8.dp),
 horizontalArrangement = Arrangement.End,
 verticalAlignment = Alignment.CenterVertically
 ) {
 IconButton(onClick = { isGrid = true }) {
 Icon(Icons.Filled.GridView, contentDescription = "Grid")
 }
 IconButton(onClick = { isGrid = false }) {
 Icon(Icons.Filled.List, contentDescription = "List")
 }
 }
 // Main Content (Photos example for now)
 if (tabTitles[selectedTab] == "Photos") {
 SectionedFilesDisplay(isGrid = isGrid)
 } else {
 // Placeholder for other categories
 Box(Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
 Text("Content for ${tabTitles[selectedTab]}", color = Color.Gray)
 }
 }
 }
 }
}

@Composable
fun SectionedFilesDisplay(isGrid: Boolean) {
 // Simulate image references using Int indices as item IDs for demonstration
 val todayItems = listOf(1, 2, 3, 4, 5, 6)
 val yesterdayItems = listOf(7, 8, 9)
 var checkedItems by remember { mutableStateOf(setOf<Int>()) }
 Column {
 FileSection(
 title = "Today",
 items = todayItems,
 checkedItems = checkedItems,
 onCheckedChange = { idx, checked ->
 checkedItems = if (checked) checkedItems + idx else checkedItems - idx
 },
 isGrid = isGrid
 )
 FileSection(
 title = "Yesterday",
 items = yesterdayItems,
 checkedItems = checkedItems,
 onCheckedChange = { idx, checked ->
 checkedItems = if (checked) checkedItems + idx else checkedItems - idx
 },
 isGrid = isGrid
 )
 // Bottom Action Bar
 Surface(shadowElevation = 2.dp) {
 Row(
 Modifier
 .fillMaxWidth()
 .padding(16.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(
 text = "${checkedItems.size} Photos Selected",
 style = MaterialTheme.typography.bodyLarge
 )
 Button(onClick = { /* handle send */ }) {
 Text("Send")
 }
 }
 }
 }
}

@Composable
fun FileSection(
 title: String,
 items: List<Int>, // Simulated image IDs
 checkedItems: Set<Int>,
 onCheckedChange: (Int, Boolean) -> Unit,
 isGrid: Boolean
) {
 Row(
 Modifier
 .fillMaxWidth()
 .padding(horizontal = 8.dp, vertical = 6.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(title, style = MaterialTheme.typography.titleMedium)
 Row(verticalAlignment = Alignment.CenterVertically) {
 Text("Select All", fontSize = 12.sp)
 Checkbox(
 checked = items.all { checkedItems.contains(it) },
 onCheckedChange = { checked ->
 if (checked) items.forEach {
 onCheckedChange(
 it,
 true
 )
 } else items.forEach { onCheckedChange(it, false) }
 },
 modifier = Modifier.padding(start = 4.dp)
 )
 }
 }
 if (isGrid) {
 LazyVerticalGrid(
 columns = GridCells.Fixed(3),
 modifier = Modifier
 .padding(horizontal = 8.dp)
 .fillMaxWidth()
 .height(160.dp)
 ) {
 items(items) { id ->
 FileCardItem(
 itemId = id,
 checked = checkedItems.contains(id),
 onCheckedChange = { checked -> onCheckedChange(id, checked) }
 )
 }
 }
 } else {
 LazyColumn(Modifier.padding(horizontal = 8.dp)) {
 items(items) { id ->
 FileCardItem(
 itemId = id,
 checked = checkedItems.contains(id),
 onCheckedChange = { checked -> onCheckedChange(id, checked) }
 )
 }
 }
 }
}

@Composable
fun FileCardItem(itemId: Int, checked: Boolean, onCheckedChange: (Boolean) -> Unit) {
 Row(
 Modifier
 .padding(vertical = 6.dp)
 .background(Color.LightGray, shape = RoundedCornerShape(8.dp))
 .clickable { onCheckedChange(!checked) }
 .padding(8.dp)
 .fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Placeholder: Use a colored box instead of actual image resource
 Box(
 Modifier
 .size(54.dp)
 .background(
 if (checked) Color(0xFFCFDEFF) else Color.White,
 shape = RoundedCornerShape(8.dp)
 ),
 contentAlignment = Alignment.Center
 ) {
 Text("Img $itemId", fontSize = 10.sp, color = Color.DarkGray)
 }
 Checkbox(checked = checked, onCheckedChange = onCheckedChange, modifier = Modifier.padding(start = 8.dp))
 // Optionally add file name or other metadata
 }
}

