fun FileSection(
 title: String,
 items: List<PhotoItem>,
 checkedItems: Set<Int>,
 onCheckedChange: (Int, Boolean) -> Unit,
 isGrid: Boolean
) {
 Row(
 Modifier
 .fillMaxWidth()
 .padding(horizontal = 8.dp, vertical = 6.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(title, style = MaterialTheme.typography.titleMedium)
 Row(verticalAlignment = Alignment.CenterVertically) {
 Text("Select ", fontSize = 12.sp)
 Checkbox(
 checked = items.all { checkedItems.contains(it.id) },
 onCheckedChange = { checked ->
 if (checked) items.forEach {
 onCheckedChange(it.id, true)
 } else items.forEach { onCheckedChange(it.id, false) }
 },
 modifier = Modifier.padding(start = 4.dp)
 )
 }
 }
 if (isGrid) {
 val configuration = LocalConfiguration.current
 val screenWidthDp = configuration.screenWidthDp.dp
 val itemMinSize = 110.dp
 val spacing = 8.dp
 val totalSpace = screenWidthDp - 16.dp // 8dp padding each side
 val itemsPerRow = maxOf(
 1,
 ((totalSpace.value + spacing.value) / (itemMinSize.value + spacing.value)).toInt()
 )
 val rows = (items.indices).chunked(itemsPerRow)
 LazyColumn(
 modifier = Modifier
 .padding(horizontal = 8.dp)
 .fillMaxWidth(),
 verticalArrangement = Arrangement.spacedBy(spacing)
 ) {
 items(rows) { rowIndices ->
 Row(
 Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(spacing)
 ) {
 rowIndices.forEach { idx ->
 val item = items[idx]
 FileCardItem(
 item = item,
 checked = checkedItems.contains(item.id),
 onCheckedChange = { checked -> onCheckedChange(item.id, checked) },
 gridMode = true,
 modifier = Modifier
 .weight(1f)
 .aspectRatio(1f)
 )
 }
 repeat(itemsPerRow - rowIndices.size) {
 Box(modifier = Modifier
 .weight(1f)
 .aspectRatio(1f)) { }
 }
 }
 }
 }
 } else {
 LazyColumn(Modifier.padding(horizontal = 8.dp)) {
 items(items) { item ->
 FileCardItem(
 item = item,
 checked = checkedItems.contains(item.id),
 onCheckedChange = { checked -> onCheckedChange(item.id, checked) },
 gridMode = false
 )
 }
 }
 }
}

@Composable
fun FileCardItem(
 item: PhotoItem,
 checked: Boolean,
 onCheckedChange: (Boolean) -> Unit,
 gridMode: Boolean,
 modifier: Modifier = Modifier
) {
 if (gridMode) {
 Box(
 modifier
 .aspectRatio(1f)
 .background(Color.LightGray, shape = RoundedCornerShape(8.dp))
 ) {
 // Placeholder for image
 Box(
 Modifier
 .fillMaxSize()
 .background(
 if (checked) Color(0xFFCFDEFF) else Color.White,
 shape = RoundedCornerShape(8.dp)
 ),
 contentAlignment = Alignment.Center
 ) {
 Text(
 item.name + item.extension,
 fontSize = 12.sp,
 color = Color.DarkGray,
 maxLines = 1,
 textAlign = TextAlign.Center
 )
 }
 // Checkbox at top right
 Box(
 Modifier
 .align(Alignment.TopEnd)
 .padding(5.dp)
 ) {
 Checkbox(
 checked = checked,
 onCheckedChange = onCheckedChange,
 modifier = Modifier.size(20.dp)
 )
 }
 }
 } else {
 Column(
 modifier
 .padding(vertical = 10.dp)
 .background(Color.LightGray, shape = RoundedCornerShape(12.dp))
 .fillMaxWidth(),
 verticalArrangement = Arrangement.Center
 ) {
 Row(
 Modifier
 .fillMaxWidth()
 .padding(horizontal = 18.dp, vertical = 10.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.weight(1f)
 ) {
 Box(
 Modifier
 .size(70.dp)
 .background(
 if (checked) Color(0xFFCFDEFF) else Color.White,
 shape = RoundedCornerShape(8.dp)
 ),
 contentAlignment = Alignment.Center
 ) {
 Text(
 item.name + item.extension,
 fontSize = 14.sp,
 color = Color.DarkGray,
 maxLines = 1,
 overflow = TextOverflow.Ellipsis,
 )
 }
 }
 // Marquee row with name+extension
 Text(
 text = item.name + item.extension,
 fontSize = 16.sp,
 maxLines = 1,
 overflow = TextOverflow.Ellipsis,
 modifier = Modifier
 .weight(2f)
 .padding(start = 18.dp)
 )
 Checkbox(
 checked = checked,
 onCheckedChange = onCheckedChange,
 modifier = Modifier.size(26.dp)
 )
 }
 // Size label below
 Text(
 text = item.size,
 fontSize = 13.sp,
 color = Color.Gray,
 modifier = Modifier.padding(horizontal = 18.dp, vertical = 0.dp)
 )
 }
 }
}

