// Gradient button used for Sender/Receiver
@Composable
fun GradientButton(
 text: String,
 subtitle: String,
 gradient: List<Color>,
 icon: Painter
) {
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(84.dp)
