/* ───────── One feature card ───────── */
@Composable
fun FeatureCard(
 modifier: Modifier = Modifier,
 title: String,
 subtitle: String,
 icon: ImageVector,
 gradientColors: List<Color>?
) {
 val dynamic = LocalDynamicColour.current
 val iconColor = if (dynamic) MaterialTheme.colorScheme.primary else Color.Unspecified
 val textColor = MaterialTheme.colorScheme.onBackground
 val subColor = MaterialTheme.colorScheme.onBackground.copy(alpha = 0.7f)

 Card(
 modifier = modifier.height(120.dp),
 shape = RoundedCornerShape(16.dp),
 colors = CardDefaults.cardColors(
 containerColor = featureColor(dynamic, MaterialTheme.colorScheme)
 )
 ) {
 Column(
 Modifier
 .fillMaxSize()
 .padding(16.dp),
 verticalArrangement = Arrangement.Center,
 horizontalAlignment = Alignment.Start
 ) {
 Box(
 modifier = Modifier
 .size(40.dp).let {
 if (gradientColors != null) {
 it.background(
 brush = Brush.linearGradient(gradientColors),
 shape = RoundedCornerShape(24.dp))
 } else it
 },
 contentAlignment = Alignment.Center
 ) {
 Icon(icon, null, tint = iconColor, modifier = Modifier.size(28.dp))
 }
 Spacer(Modifier.height(7.dp))
 Text(title,
 style = MaterialTheme.typography.titleSmall.copy(fontWeight = FontWeight.Bold),
 color = textColor
 )
 Spacer(Modifier.height(4.dp))
 Text(subtitle,
 style = MaterialTheme.typography.bodySmall,
 color = subColor,
 lineHeight = 14.sp
 )
 }
 }
}


/* ───────── Bottom nav bar ───────── */
@Composable
fun SmartBottomBar(navController: NavHostController) {
 val dynamic = LocalDynamicColour.current
 val barColor = bottomBarColor(dynamic, MaterialTheme.colorScheme)

 NavigationBar(
 containerColor = barColor, // adaptive
 tonalElevation = 3.dp
 ) {
 NavItem(Icons.Default.Home, "Home", true, onClick = { navController.navigate(Routes.Home.route) })
 NavItem(Icons.Default.SwapHoriz, "Transfer", false, onClick = { navController.navigate(Routes.FileTransfer.route) })
 NavItem(Icons.Default.History, "Files", false, onClick = { /* TODO */ })
 NavItem(Icons.Default.Person, "Profile", false, onClick = { /* TODO */ })
 }
}


@Composable
private fun RowScope.NavItem(
 icon: ImageVector,
 label: String,
 selected: Boolean,
 onClick: () -> Unit
) = NavigationBarItem(
 selected = selected,
 onClick = onClick,
 icon = { Icon(icon, contentDescription = label) },
 label = { Text(label) },
 colors = NavigationBarItemDefaults.colors(
 selectedIconColor = MaterialTheme.colorScheme.primary,
 selectedTextColor = MaterialTheme.colorScheme.primary,
 unselectedIconColor = MaterialTheme.colorScheme.onSurfaceVariant,
 unselectedTextColor = MaterialTheme.colorScheme.onSurfaceVariant,
 indicatorColor = Color.Transparent
 )
)




/* adaptive container colours */

private fun featureColor (dyn: Boolean, c: ColorScheme) = if (dyn) c.secondaryContainer else c.surfaceVariant
private fun bottomBarColor (dyn: Boolean, c: ColorScheme) = if (dyn) c.background else c.surfaceVariant
private fun iconBgColor(dynamic: Boolean, c: ColorScheme): Color =
 if (dynamic) c.primaryContainer else Color(0xFFE5F5FF)

