@Composable
fun NativeAdBlock() {


 val c = MaterialTheme.colorScheme
 val cardBg = c.surface
 val borderCol = c.outlineVariant
 val skeletonCol = c.surfaceVariant
 val textCol = c.onSurfaceVariant

 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp)
 .height(170.dp),
 shape = RoundedCornerShape(14.dp),
 colors = CardDefaults.cardColors(containerColor = cardBg),
 border = BorderStroke(1.dp, borderCol),
 elevation = CardDefaults.cardElevation(0.dp)
 ) {

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(14.dp)
 ) {

 /* --- top skeleton row --- */
 Row(verticalAlignment = Alignment.Top) {
 Box(
 modifier = Modifier
 .size(28.dp)
 .background(skeletonCol, RoundedCornerShape(6.dp))
 )
 Spacer(Modifier.width(8.dp))
 Column(Modifier.weight(1f)) {
 Box(
 Modifier
 .height(10.dp)
 .fillMaxWidth(0.7f)
 .background(skeletonCol, RoundedCornerShape(4.dp))
 )
 Spacer(Modifier.height(6.dp))
 Box(
 Modifier
 .height(10.dp)
 .fillMaxWidth(0.5f)
 .background(skeletonCol, RoundedCornerShape(4.dp))
 )
 }
 }

 Spacer(Modifier.height(10.dp))

 /* --- additional skeleton lines --- */
 Box(
 Modifier
 .height(10.dp)
 .fillMaxWidth(0.9f)
 .background(skeletonCol, RoundedCornerShape(4.dp))
 )
 Spacer(Modifier.height(6.dp))
 Box(
 Modifier
 .height(10.dp)
 .fillMaxWidth(0.6f)
 .background(skeletonCol, RoundedCornerShape(4.dp))
 )

 Spacer(Modifier.height(12.dp))

 /* --- ad rectangle --- */
 Box(
 Modifier
 .fillMaxWidth()
 .weight(1f)
 .background(skeletonCol, RoundedCornerShape(8.dp)),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "Native AD",
 color = textCol,
 style = MaterialTheme.typography.bodyMedium
 )
 }
 }
 }
}


